import { CloseOutlined, MinusOutlined, PlusOutlined } from '@ant-design/icons';
import { Button, Card, Col, Image, Row, Spin, Typography } from 'antd';
import dayjs from 'dayjs';
import React from 'react';
import { useShoppingCart } from '../../context/ShoppingCartContext';
import generateThousand from '../../ultis/generateThousand';
import './Bill.css';

const { Title } = Typography;

const Order = (props) => {

    const { data } = props;
    const { increaseCartQuantity, decreaseCartQuantity, sumPrice, removeFromCart } = useShoppingCart();
    const increase = (id) => {
        increaseCartQuantity(id)
    };
    const decrease = (id) => {
        decreaseCartQuantity(id)
    };
    const remove = (id) => {
        removeFromCart(id)
    };
    return (
        <>
            <div className='div-bg'>
                <Title level={3} style={{ fontWeight: 'bolder' }}>Order Summary</Title>
                <Row gutter={24}>
                    {data ? data.map((item) => {
                        return <Col span={24} key={item._id}>
                            <Card className='card-bg' size="small">
                                <Row gutter={24} >
                                    <Col span={6} style={{ height: '120px' }}>
                                        <Image
                                            src={item.image}
                                            width={100}
                                            style={{ borderRadius: '25px' }}
                                            fallback="data:image/png;base64,"
                                        />
                                    </Col>
                                    <Col span={12}>
                                        <p className='titPro mb-[5px]'><span className='font-normal '>Tên sản phẩm: </span> {item.productName}</p>
                                        <p className='titPro'> <span className='font-normal '>Mô tả: </span>{item.description}</p>
                                        <p className='pos-price'>{generateThousand(item.price)} VND</p>
                                    </Col>
                                    <Col span={6}>
                                        <Row style={{ display: 'flex', justifyContent: 'right' }}>
                                            <span className='txt-quantity'>{item.quantity}</span>
                                            <Col>
                                                <Button icon={<PlusOutlined />} onClick={() => increase(item.id)} className='btn-quantity' />
                                                <Button icon={<MinusOutlined />} onClick={() => decrease(item.id)} className='btn-quantity' />
                                            </Col>
                                        </Row>
                                        <Row className='btn-cancel'>
                                            <Button
                                                style={{
                                                    width: '80px',
                                                    height: '25px',
                                                    borderRadius: '5px',
                                                    border: '2px solid',

                                                }}
                                                icon={<CloseOutlined />}
                                                onClick={() => remove(item.id)}
                                            />
                                        </Row>
                                    </Col>
                                </Row>
                            </Card>

                        </Col>
                    })
                        : <Spin tip="Loading..." >
                            lcdksj
                        </Spin>
                    }
                    <Col className='flex justify-between w-full'>
                        <div>
                            <p className='text-[12px] font-bold'>Total Order:</p>
                            <p className='text-[12px] font-normal text-[#6A983C]'>Guaranteed delivery day: <span className='font-bold'>{dayjs().format("DD-MM-YYYY")}</span> </p>
                        </div>
                        <p className='font-bold text-[26px] text-[#6A983C] leading-[39px]'> {generateThousand(sumPrice())} VND</p></Col>
                    <Col>  <div style={{ color: '#A9A9A9' }}>Price can change depending on shipping method and taxes of your state</div> </Col>
                </Row>
            </div>
        </>
    )
}

export default Order